import sys
import datetime

results = {}
start_time = datetime.datetime.now()

while True:
    candidate = input("Enter candidate's name or q to continue: ")
    if candidate == "q":
        break
    elif candidate in results:
        print("Candidate already included")
    else:
        results[candidate] = 0

if len(results) == 0:
    print("No candidates entered, quitting")
    sys.exit()

end_time = datetime.datetime.now()
time_taken = end_time - start_time
print(f"Election started at {start_time}, ended at {end_time}")
print(f"Time taken for the election: {time_taken}")
